sources.components\\_summary-items\\summary-item:
.summary-items {
  margin: 2.3rem 0 2.85rem 5.2rem;
  padding: 0;
  list-style: none;

  @media screen and (max-width: 396px) {
    margin-left: 2rem;
  }

  &__item {
    position: relative;
    margin-bottom: .8rem;

    &:before {
      @include size(1.9rem);
      position: absolute;
      top: .7rem;
      left: -4rem;
      content: "";
      border-radius: 50%;
      background-color: #000;
    }
  }

  &__header,
  &__place {
    font-size: 2.3rem;
    letter-spacing: .015rem;
  }

  &__degree {
    font-weight: bold;
    margin-right: 1.3rem;
    letter-spacing: .14rem;
    text-transform: uppercase;
    color: $color-main;
  }

  &__period {
    letter-spacing: .02rem;

    @media screen and (max-width: 886px) and (min-width: 840px) {
      font-size: 1.1rem;
    }
    @media screen and (max-width: 633px) {
      font-size: 1.1rem;
      display: block;
    }
  }

  &__place {
    line-height: 1.55;
    text-transform: uppercase;
  }

  &__description {
    margin: .1rem .4rem 0 0;
    line-height: 1.25;
  }
}

sources.components\\_font-face\\_font-face.scss:
@mixin font-face($font-family, $font, $type) {
  $weight: 400;
  $style: normal;

  @if $type == bold {
    $weight: 700;
  }
  @else if $type == italic {
    $style: italic;
  }

  @font-face {
    font-family: $font-family;
    src: url("./components/_fonts/#{$font}-#{$type}-webfont.woff2") format("woff2"),
         url("./components/_fonts/#{$font}-#{$type}-webfont.woff") format("woff");
    font-weight: $weight;
    font-style: $style;
  }
}

sources.components\\page\\page.scss:
@each $font-type in bold, italic, regular {
  @include font-face(Pt_sans, pt_sans-web, $font-type);
}

@-webkit-keyframes animate-stripes {
  @include animate-stripes;
}

@-moz-keyframes animate-stripes {
  @include animate-stripes;
}

html {
  width: 1920px;

  @media screen and (max-width: 1200px) {
    font-size: 12px;
  }

  @media screen and (max-width: 840px) {
    width: auto !important;
    font-size: 10px;
  }
}

.page {
  font-family: "Pt_sans", "Open Sans", sans-serif;
  font-size: 1.5rem;
  display: flex;
  justify-content: space-between;
  box-sizing: border-box;
  margin: 0;
  padding: 7.2rem 5.6rem 7.2rem 5.9rem;

  @media screen and (max-width: 840px) {
    display: block;
    width: 100%;
  }

  &__right-column {
    margin-left: 5.8rem;
    flex-shrink: .68;

    @media screen and (max-width: 840px) {
      margin: 0;
    }
  }
}

sources.components\\_animations\\_animations.scs:
@mixin animation($browser) {
  -#{$browser}-animation: animate-stripes 3s linear infinite;
}

@mixin animate-stripes {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 80px 0;
  }
}

sources.components\\subtitle\\subtitle.scss:
@import "./_subtitle-mod.scss";

.subtitle {
  font-size: 3.8rem;
  display: flex;
  margin-top: 4rem;
  margin-bottom: 0;
  letter-spacing: .2rem;
  text-transform: uppercase;
  color: $color-main;
  align-items: center;

  @media screen and (max-width: 396px) {
    justify-content: center;
  }

  &:before {
    @include size(7.1rem);
    margin-right: 1rem;
    content: "";
    flex-shrink: 0;

    @media screen and (max-width: 528px) {
      content: none;
    }
  }

  &:after {
    height: .5rem;
    margin-left: 1.4rem;
    margin-right: 1.4rem;
    content: "";
    background-color: $color-main;
    flex-grow: 1;
    flex-shrink: 0;

    @media screen and (max-width: 396px) {
      content: none;
    }
  }
}

@include subtitle-mod(profile);

@include subtitle-mod(contact);

@include subtitle-mod(skills) {
  margin-top: 4.1rem;
}

@include subtitle-mod(education) {
  margin-top: 0;

  @media screen and (max-width: 840px) {
    margin-top: 4.5rem;
  }
}

@include subtitle-mod(experience) {
  margin-top: 4.8rem;
  margin-bottom: 4rem;
}

@include subtitle-mod(software) {
  margin-top: 4.8rem;
  margin-bottom: 1rem;
}

.page__right-column {
  .subtitle {
    letter-spacing: .25rem;

    &:before {
      margin-right: 1.6rem;
    }

    &:after {
      margin-right: 0;
    }
  }
}

sources.components\\introduce\\introduce.scss:
.introduce {
  margin-bottom: 7.6rem;
  text-align: center;

  &__photo {
    @include size(19.4rem);
    margin: 1.3rem auto;
    padding: 1.4rem;
    border: .15rem solid $color-main;
    border-radius: 50%;
    background-color: $color-main;
    background-clip: content-box;

    @media screen and (max-width: 396px) {
      @include size(11rem);
    }
  }

  &__name {
    font-size: 5.2rem;
    font-weight: normal;
    line-height: .85;
    margin-top: 3.8rem;
    margin-bottom: 0;
    text-transform: uppercase;

    @media screen and (max-width: 396px) {
      font-size: 2.5rem;
    }
  }

  &__surname {
    font-size: 5.7rem;
    font-weight: bold;
    line-height: .8;
    margin: 0;
    letter-spacing: .35rem;
    text-transform: uppercase;

    @media screen and (max-width: 396px) {
      font-size: 2.7rem;
    }
  }

  &__profession {
    font-size: 2.45rem;
    font-weight: bold;
    line-height: .8;
    margin-top: 1.8rem;
    text-transform: uppercase;
  }
}

