sources.components\\contact\\contact.scss:
.contact {
  &__list {
    margin-top: .5rem;
    font-size: 1.9rem;
    display: table;
    border-spacing: 0;

    @media screen and (max-width: 840px) {
      margin: 0 auto;
    }
  }

  &__row {
    display: table-row;
  }

  &__description {
    font-weight: bold;
    display: table-cell;
    width: 41%;
    padding-top: 2.6rem;
    text-transform: uppercase;
    color: $color-main;
    letter-spacing: .07rem;

    @media screen and (max-width: 396px) {
      padding-right: .2rem;
    }
  }

  &__value {
    display: table-cell;
  }

  a.contact__value {
    text-decoration: none;
    color: #000;

    &:hover {
      text-decoration: underline;
      color: $color-main;
    }
  }
}

sources.components\\skills\\skills.scss:
.skills {
  &__list {
    margin-top: 2rem;
  }

  &__name {
    font-size: 2.5rem;
    margin-top: 1.55rem;
    margin-bottom: .6rem;
    text-align: center;
    text-transform: uppercase;
  }

  &__level {
    line-height: .7;
    margin: 0;
    padding-left: 0;
    text-align: center;
  }

  &__level-item {
    display: inline-block;
    margin-right: .46rem;

    @media screen and (max-width: 960px) {
      margin-right: .3rem;
    }
  }

  &__dot {
    display: block;
    border-radius: 50%;
    background-color: $color-main;
    @include size(1.8rem);

    &--gray {
      background-color: $color-dots;
    }
  }
}

sources.components\\software\\software.scss:
.software-flex {
  display: flex;
  justify-content: space-between;
  align-content: flex-start;
  flex-wrap: wrap;
  height: 28rem;
  margin-left: .7rem;
  margin-right: .3rem;

  @media screen and (max-width: 528px) {
    display: block;
  }
}

.software {
  &__item {
    flex-basis: 44.2%;
    margin-bottom: .8rem;

    @media screen and (max-width: 528px) {
      width: 100%;
    }
  }

  &__name {
    font-size: 2.5rem;
    margin-top: 1.17rem;
    margin-bottom: 1.05rem;
    letter-spacing: .02rem;
    text-transform: uppercase;
  }

  &__level {
    height: 2.1rem;
    background-color: $color-dots;

    span {
      display: inline-block;
      height: 100%;
      transition: width .4s ease-in-out;
      background-color: $color-main;
      background-size: 40px 40px;
      @include animation(webkit);
      @include animation(moz);
      @include linear-gradient($color-gradient, 25%);
    }
  }
}

sources.components\\_linear-gradient\\_linear-gr:
@mixin linear-gradient($color, $percent, $deg: 135deg) {
  $color-stops: $color $percent, transparent $percent, transparent $percent*2, $color $percent*2, $color $percent*3, transparent $percent*3, transparent;

  @include gradient-bg($deg, $color-stops);
}

@mixin gradient-bg($deg, $color-stops...) {
  background-image: linear-gradient($deg, $color-stops);
}
